import { Component } from '@angular/core';


@Component({
  selector: 'app-contact',
  templateUrl: './contact.component.html',
  styleUrls: ['./contact.component.css']
})
export class ContactComponent {

isEmpty: boolean[]=[true,true,true,true];
show(event:any){
  switch(event.target.value){
    case'':
    switch(event.target.id){
      case'userName':
      this.isEmpty[0]=true;
      break;
      case'userAge':
      this.isEmpty[1]=true;
      break;
      case'userEmail':
      this.isEmpty[2]=true;
      break;
      case'userPas':
      this.isEmpty[3]=true;
      break;
      default:
        break;
    }
    break;
    default:
        switch(event.target.id){
          case'userName':
          this.isEmpty[0]=false;
          break;
          case'userAge':
          this.isEmpty[1]=false;
          break;
          case'userEmail':
          this.isEmpty[2]=false;
          break;
          case'userPas':
          this.isEmpty[3]=false;
          break;
          default:
            break;
        }
        break;
      }
    }
}
<div class="justify-content-center align-items-center text-white mt-5 d-flex w-100 h-100 pt-5">
    <div class="text-center">
        <h2 class="text-uppercase mb-3 fw-bolder text-center pt-5 fs-1">CONATCT SECTION</h2>
        <div class="title d-flex align-items-center justify-content-center mb-3">
            <div class="li text-center me-4"></div>
            <i class="fa-solid fa-star"></i>
            <div class="li text-center ms-4"></div>
           </div>
</div>
</div>
         <form class="w-50 p-3 m-auto">
        <label [style]="!isEmpty[0] ? 'transform: translateY(-45px)' :'transform: translateX(0)'" for="userName" class="position-relative top-50 mb-3">userName : </label>
        <input (keydown)="show($event)" id="userName" type="text" placeholder="userName" name="userName" class="form-control border-0 border-bottom py-3 position-relative">

        <label [style]="!isEmpty[1] ? 'transform: translateY(-45px)' :'transform: translateX(0)'" for="userAge" class="position-relative top-50 mb-3">userAge : </label>
        <input  (keydown)="show($event)" id="userAge" type="number" placeholder="userAge" name="userName" class="form-control border-0 border-bottom py-3 position-relative">

        <label [style]="!isEmpty[2] ? 'transform: translateY(-45px)' :'transform: translateX(0)'" for="userEmail" class="position-relative top-50 mb-3">userEmail : </label>
        <input  (keydown)="show($event)"id="userEmail" type="email" placeholder="userEmail" name="userName" class="form-control border-0 border-bottom py-3 position-relative">

        <label [style]="!isEmpty[3] ? 'transform: translateY(-45px)' :'transform: translateX(0)'" for="userPassword" class="position-relative top-50 mb-3">userPassword : </label>
        <input  (keydown)="show($event)"id="userPas" type="password" placeholder="userPassword" name="userName" class="form-control border-0 border-bottom py-3 position-relative">
        <button class="btn text-white mt-3"> send Message </button>
    </form>
